mapper.select:
    select

    from ad


	       type= #{type,jdbcType=TINYINT}
	       and status=2
	       order by hits desc,gmt_create desc



	       type=1 and  status > 0 and status < 3 and begin_time > #{beginAfterTime,jdbcType=TIMESTAMP} 
	       order by total_point desc 




	      	 type=1 and  status > 0 and status < 3 and begin_time > #{beginAfterTime,jdbcType=TIMESTAMP}  
	      	 and point>=20
	      	 order by point desc
