mapper.select:
select
	sum(point) as user_totle_point
from point_detail
where
  	point_type =#{pointType,jdbcType=TINYINT}
  	and biz_id=#{bizId,jdbcType=BIGINT}

select
	sum(amount) as love_totle_point
from love_detail
where
  	love_type =#{pointType,jdbcType=TINYINT}
  	and biz_id=#{bizId,jdbcType=BIGINT}
