mapper.update:
UPDATE merchant_store
SET buy_numbers = #{buyNums}
WHERE merchant_id = #{merchantId} AND buy_numbers < #{buyNums}

UPDATE merchant_store
SET comments_count = #{commentsCount}
WHERE
merchant_id = #{merchantId} AND comments_count < #{commentsCount}

mapper.select:
SELECT ms.id FROM merchant_store ms INNER JOIN merchant_store_profile msp on ms.merchant_id = msp.merchant_id INNER JOIN merchant m on ms.merchant_id = m.id WHERE (ms.status != #{status} or msp.manage_type != #{manageType} or m.is_freeze = 1)
and ms.gmt_modified >= date_sub(now(), interval 2 hour)
limit #{offset} , #{pageSize}
